mapper.select:
SELECT NVL(COUNT(*), 0) 
FROM ACLAPMEMBER
WHERE EMAIL=#{email}

SELECT NVL(COUNT(*), 0) 
FROM ACLAPMEMBER
WHERE NICKNAME=#{nickName}

SELECT *
FROM ACLAPMEMBER
WHERE EMAIL=#{email} AND PWD=#{pwd}

mapper.update:

UPDATE ACLAPMEMBER
SET PWD=#{pwd}
WHERE EMAIL=#{email}


UPDATE ACLAPMEMBER
SET DEL=1
WHERE MEMNUM=#{memNum}


UPDATE ACLAPMEMBER
SET CLASSMASTER=1
WHERE MEMNUM=#{memNum}


UPDATE ACLAPMEMBER
SET MYPOINT=MYPOINT+1
WHERE MEMNUM=#{memNum}


UPDATE ACLAPMEMBER
SET INTEREST1=#{interest1} 

, INTEREST2=#{interest2}
, INTEREST3=#{interest3}    

WHERE MEMNUM=${memNum}

mapper.select:
SELECT MEMNUM, EMAIL, PWD, USERNAME, NICKNAME, PHONENUM, INTEREST1, INTEREST2, INTEREST3, PROFILEPIC, AUTH, CLASSMASTER, MYPOINT
FROM ACLAPMEMBER
WHERE MEMNUM = #{ memNum }

mapper.update:
UPDATE ACLAPMEMBER
SET MYPOINT=MYPOINT+#{money}
WHERE EMAIL=#{email}
