mapper.select:
SELECT COUNT(MEMNUM)
FROM LIKES
WHERE MEMNUM=#{memNum} AND CLASSNUM=#{classNum}

mapper.update:
UPDATE ONEDAYCLASS
SET LIKECOUNT = LIKECOUNT + 1
WHERE CLASSNUM=#{classNum}

UPDATE ONEDAYCLASS
SET LIKECOUNT = LIKECOUNT - 1
WHERE CLASSNUM=#{classNum}

mapper.select:
SELECT NVL(COUNT(*), 0)
FROM LIKES
WHERE MEMNUM=#{memNum}

SELECT NVL(COUNT(*), 0)
FROM LIKES
WHERE CLASSNUM=#{classNum}
